package gui;import java.awt.*;import java.awt.event.*;public class AdaptiveLog extends DoubleLog {private NegateFrame parent;private static final String title = "Adaptive Histogram Equalization";private static final String prompts[] ={"rows=","cols="};private static final String defaults[] ={"2","2"};private static final int fieldSize = 5;public AdaptiveLog(		NegateFrame _parent){		super(_parent, title, prompts, defaults,fieldSize);	parent = _parent;	}public void actionPerformed(ActionEvent e) {  if (e.getSource() == setButton) {  	double dui[] = getUserInputAsDouble();  	parent.drawMosaic((int)dui[0],(int)dui[1]);  	return;  }  super.actionPerformed(e);}}